package threads;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import java.math.BigInteger;

import static org.hamcrest.MatcherAssert.assertThat;
import static org.hamcrest.Matchers.equalTo;
import static org.junit.jupiter.api.Assertions.*;

class ComplexCalculationTest {

    @BeforeEach
    void setUp() {
    }

    @Test
    void calculateResult() throws InterruptedException {
        ComplexCalculation calc = new ComplexCalculation();
        BigInteger result = calc.calculateResult(BigInteger.valueOf(2), BigInteger.valueOf(2), BigInteger.valueOf(2), BigInteger.valueOf(2));
        assertThat(result, equalTo( BigInteger.valueOf(8) ));
    }

    @Test
    void calculateResult2() throws InterruptedException {
        ComplexCalculation calc = new ComplexCalculation();
        BigInteger result = calc.calculateResult(BigInteger.valueOf(789), BigInteger.valueOf(10000), BigInteger.valueOf(2), BigInteger.valueOf(2));
        assertThat(result, equalTo( new BigInteger("")));
    }
}